import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';
import React from "react";

const Index = (props) => {
    return (
        <Layout>
            <h1 style={{ textAlign:'center'}}>WELCOME TO USER PAGE</h1>
            {props.users.map((x)=>{
                return(
                       <ul style={{border:'solid', borderColor:'gray' , padding:'10px' , margin:'40px', listStyle:'none'}}>
                           <li> Id:{x.id}</li>
                           <li>Name : {x.name}</li>
                           <li>Email : {x.email}</li>
                           <li>Description : {x.description}</li>  
                           <li>skill_level: {x.skill_level}</li>
                       </ul>
                )
            })}
        </Layout>
    )
}
React.useEffect(() => {
    getInfo();
  }, []);
const getInfo = async () => {
    const response = await fetch('http://127.0.0.1:8000/api/v1/description/');
    const result = await response.json();
    console.log(result);
    setBooks(result);
  };
Index.getInitialProps = async (props) => {
    let res, data, users;
    try{
        res = await fetch('http://127.0.0.1:8000/api/v1/user/');
        data = await res.json();
        users = data.objects
    } catch(error){
        console.error(error);
    }
    return {
        users
    }
};
export default Index;
