import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';


const Index = (props) => {
    console.log('This is the Index Page');
    return (
        <Layout>
            {props.users.map((x)=>{

                return(
                   <table style={{padding:'10px'}}>
                       <tr style={{marginLeft:'20px'}}>
                           <td >{x.name}</td>
                           <td>{x.email}</td>
                       </tr>

                   </table>
                )
            })}
               {/* <h4>name: {props.users[0].name}</h4>
                <h6>email: {props.users[0].email}</h6> */}
        </Layout>
    )
};


Index.getInitialProps = async (props) => {
    let res, data, users;
    try{
        res = await fetch('http://127.0.0.1:8000/api/v1/user/');
        data = await res.json();
        users = data.objects.all()
    } catch(error){
        console.error(error);
    }
    return {
        users
    }
};
export default Index;
